let cp = require('child_process');

// executing any application or file....
cp.execFileSync('calc');
// for vs code
cp.execSync('code');
//for starting chrome
cp.execSync('start chrome');

//for running a file
console.log("the file is running",cp.execSync('node 2_variable.js')+""); // normal way
let data = cp.execSync('node 2_variable'); // using utf-8 encoding
console.log(data.toString('utf-8')); 





