// ðŸ”¥ The Path module provides a way of working with directories and file paths.

let path = require('path');
console.log(path);

// basename --> returns the basename 
let basename = path.basename(__filename);
console.log("basename --> " +basename);

// extname ---> return the extension of the file ..
let ext = path.extname("C:\Users\Gaurav Sharma\OneDrive\Desktop\javascript\1_intro.js");
console.log("extension name --> " + ext);

// __dirname --> gives the path of current working directory or path of current file's directory...
console.log("dirname --> "+__dirname);

// __filename ---> gives the path of current file..
console.log("filename -->"+ __filename);

// The path.join() method joins the specified path segments into one path. The specified path segments must be strings, separated by comma.
let dirPath = __dirname;
let newPath = path.join(dirPath,"2_os.js");
console.log(newPath);
