const { dir } = require('console');
let fs = require('fs');
let path = require('path');


// ðŸ”¥ðŸ”¥ for file
let filePath = path.join(__dirname,"file.txt");

// C - create
fs.writeFileSync(filePath,"hello bro maine tere ander text daal dia"); // it will write into a file if it exists and override the data into the file and if file doesn't exists then it will create a one and then write into that file...

// R - read
let content = fs.readFileSync(filePath,"utf-8"); // it will read file and give output
console.log(content);

// U - update
fs.appendFileSync(filePath,"\nThis is the new data which is used to append the data at the end"); // it will append the data to the end of the file
console.log(fs.readFileSync(filePath,"utf-8"));

// D - delete
fs.unlinkSync(filePath);


// ðŸ”¥ðŸ”¥ for directory

let newDirPath = path.join(__dirname,"new folder");


// C - create
fs.mkdirSync(newDirPath);

let newFilePath1 = path.join(newDirPath,"first.txt");
let newFilePath2 = path.join(newDirPath,"second.txt");
fs.writeFileSync(newFilePath1,"mai hoon file no. 1");
fs.writeFileSync(newFilePath2,"mai hoon file no. 2");

// R - read
console.log(fs.readdirSync(newDirPath));

// D - delete
fs.unlinkSync(newFilePath1);
fs.unlinkSync(newFilePath2);
fs.rmdirSync(newDirPath);